<template>
    <div class="container">
        <h2> Sensor Detail </h2>
        <table>
            <tr>
                <td>Name:</td>
                <td>{{ sensor.name }}</td>
            </tr>
            <tr>
                <td>Metrics:</td>
                <td>{{ sensor.metrics }}</td>
            </tr>
            <tr>
                <td>Serial Number:</td>
                <td>{{ sensor.serial_number }}</td>
            </tr>
            <tr>
                <td>Type:</td>
                <td>{{ sensor.sensor_type }}</td>
            </tr>
            <tr>
                <td>Unit:</td>
                <td>{{ sensor.unit }}</td>
            </tr>
        </table>
    </div>
</template>
  
<script>
import axios from 'axios';

export default {
    name: 'SensorDetail',
    data() {
        return {
            sensor: {},
        };
    },
    methods: {
        getSensorDetails() {
            const id = this.$route.params.id;
            axios.get(`http://localhost:8000/api/sensors/${id}/`).then((response) => {
                this.sensor = response.data;
            });
        },
    },
    created() {
        this.getSensorDetails();
    },
};
</script>
  
<style scoped>
table,
th,
td {
    border: 1px solid black;
    border-collapse: collapse;
}

th,
td {
    padding: 5px;
}

.container {
    margin: 20px;
}
</style>
